import axios from "axios";
import {
  LoginSocialGoogle,
  LoginSocialFacebook,
  IResolveParams,
} from 'reactjs-social-login';

import { GoogleLogin } from '@react-oauth/google';
import {
  FacebookLoginButton,
  GoogleLoginButton,
} from 'react-social-login-buttons';
const baseURL = "https://tiny-bird-36835.botics.co/api/v1/users/google/";
const redirect_url = "https://tiny-bird-36835.botics.co/";
import react,{useCallback,useEffect,useState} from 'react';

const handleGoogleLogin = (response) => {
  axios
    .post(`${baseURL}`, {
      token: response.credential,
      backend: "google-oauth2",
      grant_type: "convert_token",
      client_id: drfClientId,
      client_secret: drfClientSecret,
    })
    .then((res) => {
      console.log("result",res)
    })
    .catch((err) => {
      console.log("Error Google login", err);
    });
};
const responseMessage = (response) => {
  console.log(response);
};
const errorMessage = (error) => {
  console.log(error);
};
const [provider, setProvider] = useState('');
const [profile, setProfile] = useState('');
const onLoginStart = useCallback(() => {
  alert('login start');
}, []);

const App = () => {
  return (
    <div className="App">
      <LoginSocialFacebook
          appId="450929960465142"
          fieldsProfile={
            'id,first_name,last_name,middle_name,name,name_format,picture,short_name,email,gender'
          }
          onLoginStart={onLoginStart}
          redirect_uri={redirect_url}
          onResolve={(response) => {
            console.log("response",response)
          }}
          onReject={err => {
            console.log(err);
          }}
        >
          <FacebookLoginButton />
        </LoginSocialFacebook>
      <LoginSocialGoogle
          client_id="968378738153-bgi29e57md0mgl2u4nrqau1ksvdre34s.apps.googleusercontent.com"
          onLoginStart={onLoginStart}
          redirect_uri={redirect_url}
          scope="openid profile email"
          discoveryDocs="claims_supported"
          access_type="offline"
          onResolve={(response) => {
            console.log("response",response)
          }}
          onReject={err => {
            console.log(err);
          }}
        >
          <GoogleLoginButton />
        </LoginSocialGoogle>
    </div>
  );
};

export default App;
