import GoogleLogin from "react-google-login";
import axios from "axios";
// get env vars
const googleClientId = "968378738153-bgi29e57md0mgl2u4nrqau1ksvdre34s.apps.googleusercontent.com";
const drfClientSecret = process.env.REACT_APP_DRF_CLIENT_SECRET;
const baseURL = "https://tiny-bird-36835.botics.co/api/v1/users/google/";

const handleGoogleLogin = (response) => {

  window.gapi.load('client:auth2', () => {
    window.gapi.client.init({
        clientId: googleClientId,
  })
})

  console.log("response",response)
  axios
    .post(`${baseURL}`, {
      access_token: response.accessToken,
    })
    .then((res) => {
      console.log("result",res)
    })
    .catch((err) => {
      console.log("Error Google login", err);
    });
};

const App = () => {
  return (
    <div className="App">
      <GoogleLogin
        clientId={googleClientId}
        buttonText="LOGIN WITH GOOGLE"
        onSuccess={(response) => handleGoogleLogin(response)}
        render={(renderProps) => (
          <button
            onClick={renderProps.onClick}
            disabled={renderProps.disabled}
            type="button"
            class="login-with-google-btn"
          >
            Sign in with Google
          </button>
        )}
        onFailure={(err) => console.log("Google Login failed", err)}
      />
    </div>
  );
};

export default App;
